const INITIAL_STATE = {
  hidden: true,
};

const cartReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "TOGGLE_CART":
      if (hidden) {
        return {
          ...state,
          hidden: false,
        };
      }
    default:
      return state;
  }
};

export default cartReducer;
