import React, { Component } from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";

import CollectionPage from "../collection-page/collection-page.component";
import CollectionOverview from "../../components/collection-overview/collection-overview.component";

import {
  firestore,
  convertCollectionsSnapshotToMap,
} from "../../firebase/firebase.utils";
import { addDataToReducer } from "../../redux/shop-items/shop-item.actions";

class ShopPage extends Component {
  unsubscribeFromSnapshot = null;

  componentWillMount() {
    const collectionRef = firestore.collection("collections");

    collectionRef.onSnapshot(async (collectionsSnapshot) => {
      console.log(collectionsSnapshot);
      const data = await convertCollectionsSnapshotToMap(collectionsSnapshot);
      this.props.addDataToReducer(data);
    });
  }

  render() {
    const { match } = this.props;
    return (
      <div className="shop-page">
        <Route exact path={`${match.url}`} component={CollectionOverview} />
        <Route path={`${match.url}/:collectionId`} component={CollectionPage} />
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  addDataToReducer: (data) => dispatch(addDataToReducer(data)),
});

export default connect(null, mapDispatchToProps)(ShopPage);
