import { createSelector } from "reselect";

const shopItemsSelector = (state) => state.shopItems;
// select the reducor first

export const selectCollections = createSelector(
  [shopItemsSelector],
  (shopItems) => shopItems.collections
);

export const selectCollection = (params) =>
  createSelector([selectCollections], (items) =>
    items.find((item) => item.routeName === params)
  );
