import { createSelector } from "reselect";

const shopItemsSelector = (state) => state.shopItems;
// select the reducor first

export const selectItems = createSelector(
  [shopItemsSelector],
  (shopItems) => shopItems.items
);

export const collectionSelector = (params) =>
  createSelector([shopItemsSelector], (items) =>
    items.find((item) => item.routeName === params)
  );
