import React from "react";

function FormInput({ handleChange, label, ...otherprops }) {
  return (
    <div className="group">
      <input onChange={handleChange} {...otherprops}></input>;
      {label ? (
        <label
          className={`${
            othersprops.value.length ? "shrink" : null
          } form-input-label`}
        ></label>
      ) : null}
    </div>
  );
}
export default FormInput;
